  renderPieChart(): void {
    this.receiveMarketData().subscribe((data: company[]) => {
      for (let i = 0; i < data.length; i++) {
        if (data[i].change > 0) {
          this.y[0]++;
        }
        else if (data[i].change < 0) {
          this.y[1]++;
        }
        else { this.y[2]++; }
      }

      const categories = ['Gainers', 'Losers', 'Neutral'];
      const colours = ['#00FF00', '#FF0000', '#0000FF'];
      console.log(this.y);
      this.pieChart = {
        chart: {
          type: 'pie',
          height: '100%',
          width: '100%',
        },
        series: this.y,
        labels: categories,
        colors: colours
      };
    })
  }


  renderPieChart2(): void {
    this.receiveMarketData().subscribe((data: company[]) => {
      for (let i = 0; i < data.length; i++) {
        if (data[i].change > 0) {
          this.y[0]++;
        }
        else if (data[i].change < 0) {
