      };
    })
  }

  renderDataTable(): void {
    this.dtOptions = {
      columnDefs: [
        { width: '60em', targets: 0 },
        { width: '30em', targets: [1, 2, 3, 4, 5] },

      ],
      columns: [
        {
          title: 'Particulars',
          data: '',
        },
        {
          title: '2022',
          data: '2022',
        },
