import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import {
  ApexAxisChartSeries,
  ApexChart,
  ApexDataLabels,
  ApexPlotOptions,
  ApexYAxis,
  ApexLegend,
  ApexStroke,
  ApexXAxis,
  ApexFill,
  ApexTooltip,
} from 'ng-apexcharts';
import { Observable } from 'rxjs';
import { MACD } from 'src/app/services/technical-indicators.service';


export type ChartOptions = {
  series: ApexAxisChartSeries;
  chart: ApexChart;
  dataLabels: ApexDataLabels;
  plotOptions: ApexPlotOptions;
  yaxis: ApexYAxis;
  xaxis: ApexXAxis;
  fill: ApexFill;
  tooltip: ApexTooltip;
  stroke: ApexStroke;
  legend: ApexLegend;
};

@Component({
  selector: 'app-graph',
  templateUrl: './graph.component.html',
  styleUrls: ['./graph.component.css']
})
export class GraphComponent implements OnInit {
  y: number[] = Array(3).fill(0);
  public lineGraph: Partial<ChartOptions> | any;
  public lineGraph2: Partial<ChartOptions> | any;
  public lineGraph3: Partial<ChartOptions> | any;
  public lineGraph4: Partial<ChartOptions> | any;
  public lineGraph5: Partial<ChartOptions> | any;
  public lineGraph6: Partial<ChartOptions> | any;

  code: string = "GP";
  dateFrom: string = "2022-03-19";

  baseUrl = 'http://localhost:4000/api/MACD/';
  baseUrl2 = 'http://localhost:4000/api/SMA50/';
  baseUrl3 = 'http://localhost:4000/api/EMA50/';
  baseUrl4 = 'http://localhost:4000/api/STOCH/';
  baseUrl5 = 'http://localhost:4000/api/RSI/';
  baseUrl6 = 'http://localhost:4000/api/BB/';

  constructor(private http: HttpClient) { }
  ngOnInit(): void {
    this.getMACD();
  }

  getMACD() {
    // const data = { code: this.code, dateFrom: this.dateFrom };
    //   this.http.post(this.baseUrl, data)
    //   .subscribe((res: any) => {
     this.http.get(`${this.baseUrl}?code=${this.code}&dateFrom=${this.dateFrom}`).subscribe({
      // (res) => ({
        next: (res: any) =>{
        console.log(res);

        this.lineGraph = {
          chart: {
            type: 'area',
            height: '140%',
            width: '100%',
            zoom: {
              type: 'x',
              enabled: true,
              autoScaleYaxis: true,
            },
          },
          series: [{
            name: 'MACD',
            data: res["MACD"],
          },

          ],
          xaxis: {
            categories: res["Date"],
          },
        };
        this.lineGraph.render();
  }})

  }


}
<div id="g1">
  <h2>MACD</h2>
    <apx-chart [series]="lineGraph.series" [chart]="lineGraph.chart" 
  [xaxis]="lineGraph.xaxis"></apx-chart>
</div>

<div id="g2">
  <h2>SMA50</h2>
    <apx-chart [series]="lineGraph2.series" [chart]="lineGraph2.chart" 
  [xaxis]="lineGraph2.xaxis"></apx-chart>
</div>

<div id="g3">
  <h2>EMA50</h2>
    <apx-chart [series]="lineGraph3.series" [chart]="lineGraph3.chart" 
  [xaxis]="lineGraph3.xaxis"></apx-chart>
</div>

<div id="g4">
  <h2>STOCK</h2>
    <apx-chart [series]="lineGraph4.series" [chart]="lineGraph4.chart" 
  [xaxis]="lineGraph4.xaxis"></apx-chart>
</div>

<div id="g5">
  <h2>RSI</h2>
    <apx-chart [series]="lineGraph5.series" [chart]="lineGraph5.chart" 
  [xaxis]="lineGraph5.xaxis"></apx-chart>
</div>

<div id="g6">
  <h2>BB</h2>
    <apx-chart [series]="lineGraph6.series" [chart]="lineGraph6.chart" 
  [xaxis]="lineGraph6.xaxis"></apx-chart>
</div>
