import { Component, OnInit } from '@angular/core';
import { MarketDataService } from 'src/app/services/market-data.service';
import { TechnicalIndicatorsService } from 'src/app/services/technical-indicators.service';
import { company, sector } from 'src/app/services/market-data.service';
import { Observable } from 'rxjs';
import {
  ApexAxisChartSeries,
  ApexChart,
  ApexDataLabels,
  ApexPlotOptions,
  ApexYAxis,
  ApexLegend,
  ApexStroke,
  ApexXAxis,
  ApexFill,
  ApexTooltip,
} from 'ng-apexcharts';


export type ChartOptions = {
  series: ApexAxisChartSeries;
  chart: ApexChart;
  dataLabels: ApexDataLabels;
  plotOptions: ApexPlotOptions;
  yaxis: ApexYAxis;
  xaxis: ApexXAxis;
  fill: ApexFill;
  tooltip: ApexTooltip;
  stroke: ApexStroke;
  legend: ApexLegend;
};

@Component({
  selector: 'app-graph2',
  templateUrl: './graph2.component.html',
  styleUrls: ['./graph2.component.css']
})
export class Graph2Component implements OnInit{
  ngOnInit(): void {
    this.renderIndiceGraph2("dsex");
  }
  public lineGraph: Partial<ChartOptions> | any;

  constructor(private MarketDataService: MarketDataService,private TecIndSer: TechnicalIndicatorsService) { }

  receiveDseIndices(): Observable<any> {
    return this.MarketDataService.getIndices();

  }

  receiveMACD(): Observable<any> {
    return this.TecIndSer.getMACD();

  }

  renderIndiceGraph2(index: string): void {

    this.receiveMACD().subscribe((data1) => {
      const data = Object.entries(data1).map(([x, y]) => ({ x: parseInt(x), y: y }));

      this.lineGraph = {
        chart: {
          type: 'area',
          height: '140%',
          width: '90%',
          zoom: {
            type: 'x',
            enabled: true,
            autoScaleYaxis: true,
          },
        },
        series: [{
          name: 'Index',
          data: data,
        }],
        xaxis: {
          type: 'datetime',
          labels: {
            format: 'dd/MM',
          }
        },
      };
      this.lineGraph.render();
    })
  }
}
