import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormControl, FormGroup, Validators } from '@angular/forms';


@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css']
})

export class SignUpComponent {
  bo: string = '';
  email: string = '';
  password: string = '';
  password2: string = '';
  Phone: string = '';
  OTP: string = '';
  showOtp: boolean = false;
  showPhone: boolean = true;
  verifyOtp: boolean = true;
  verified: boolean = false;
  submitted: boolean =false;

  baseUrl = 'http://localhost:4000/api/verifyPhone/';
  baseUrl1 = 'http://localhost:4000/api/checkotp/';
  baseUrl2 = 'http://localhost:4000/api/create-account/';

  signupForm: FormGroup;

  constructor(private http: HttpClient) { 
  this.signupForm = new FormGroup({
    bo: new FormControl('', [Validators.required, Validators.pattern('[0-9]{20}')]),
    Phone: new FormControl('', [Validators.required, Validators.pattern('[0-9]{11}')]),
    otp: new FormControl('', Validators.required),
    password: new FormControl('', Validators.required),
    password2: new FormControl('', Validators.required)
  });
}

  sendPhone() {
    this.http.post(this.baseUrl, { phone: this.Phone }).subscribe(
      (response) => {
        this.showOtp = true;
        this.showPhone = false;
      },
      (error) => {
        console.error('Error:', error);
      }
    );
  }

  sendOtp() {
    this.http.post(this.baseUrl1, { otp: this.OTP }).subscribe({
      next: (res) => {
        if (res == "1") {
          this.verified = true;
        }
        else { this.verifyOtp = false; }
      },
      error: (e) => {
        console.log('Error:', e);
      }
    });
  }

  matchPassword() {
    if (this.password == this.password2) {
      const data = { BO: this.bo, phone: this.Phone, password: this.password };
      this.http.post(this.baseUrl2, data)
        .subscribe((response: any) => {
          if (response == "1") {
            alert('Sign up successful');
          } else {
            alert('Sign up failed: ' + response.error);
          }
        });
    }
    else {
      alert('Password does not match.');
    }
  }
}
