import { Component, OnInit } from '@angular/core';
import { MarketDataService } from 'src/app/services/market-data.service';
import { company } from 'src/app/services/market-data.service';
import {
  ApexAxisChartSeries,
  ApexChart,
  ChartComponent,
  ApexDataLabels,
  ApexPlotOptions,
  ApexYAxis,
  ApexLegend,
  ApexStroke,
  ApexXAxis,
  ApexFill,
  ApexTooltip,
} from 'ng-apexcharts';
import { ActivatedRoute } from '@angular/router';
import { DatePipe } from '@angular/common';
import { Observable } from 'rxjs';
import * as moment from 'moment';

export type ChartOptions = {
  series: ApexAxisChartSeries;
  chart: ApexChart;
  dataLabels: ApexDataLabels;
  plotOptions: ApexPlotOptions;
  yaxis: ApexYAxis;
  xaxis: ApexXAxis;
  fill: ApexFill;
  tooltip: ApexTooltip;
  stroke: ApexStroke;
  legend: ApexLegend;
};

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit {

  dtOptions: DataTables.Settings = {};
  dataAvail = true;
  public spinner: boolean = true;
  y: number[] = Array(3).fill(0);
  public search: string = '';
  public lineGraph: Partial<ChartOptions> | any;
  public pieChart: Partial<ChartOptions> | any;

  constructor(private MarketDataService: MarketDataService) {}


  ngOnInit(): void {
    this.renderDataTable();
    this.receiveMarketData().subscribe((data) => {
      data = data.filter(function (dat: any) {
        return dat.trading_code != '';
      });
      this.spinner = false;
      this.dtOptions.data = data;
      this.dataAvail = true;
    });

    this.renderIndiceGraph("dsex");
    this.renderPieChart();

    this.renderIndiceGraph("dses");
    this.renderIndiceGraph("ds30");


  }

  receiveMarketData(): Observable<any> {
    return this.MarketDataService.getMarketData();
  }

  receiveDseIndices(): Observable<any> {
    return this.MarketDataService.getIndices();

  }


  renderDataTable(): void{
    this.dtOptions = {
      // searching: true,
      lengthChange: false,
      language: {
        paginate: { next: '>', last: 'Last', first: 'First', previous: '<' },
        searchPlaceholder:"Search",
        search:""
      },
      pageLength: 20,
      columnDefs: [
        { width: '30em', targets: [0, 1, 2, 3, 4] },
        { name: 'some name', targets: 0 },
        { orderable: true, targets: [0, 1] },
      ],
      //  data:[{"trading_code":"First Janata Bank Mutual Fund","ltp":6.2,"closep":6.2,"change":-0.1,"ycp":6.3},{"trading_code":"Prime Finance First Mutual Fund","ltp":17.2,"closep":17.2,"change":-0.1,"ycp":17.3},{"trading_code":"aamra networks limited","ltp":39,"closep":39,"change":0.3,"ycp":38.7},{"trading_code":"aamra technologies limited","ltp":35.8,"closep":35.8,"change":0.2,"ycp":35.6},{"trading_code":"AB Bank 1st Mutual fund","ltp":5.6,"closep":5.6,"change":0,"ycp":5.6},{"trading_code":"AB Bank Limited","ltp":10,"closep":10,"change":0,"ycp":10},{"trading_code":"Aman Cotton Fibrous Limited","ltp":27.7,"closep":27.7,"change":-0.3,"ycp":28},{"trading_code":"ACI  Limited","ltp":278.3,"closep":278.3,"change":3,"ycp":275.3}],
      columns: [
        {
          title: 'CODE',
          data: 'trading_code',
          render: function (data, type, row, meta) {
            row.trading_code = row.trading_code.replace(/ /g, '-');
            if (type === 'display') {
              if (row.change < 0) {
                data =
                  '<a style="color:red;" href="' +
                  row.scrip +
                  '/' +
                  row.trading_code +
                  '">' +
                  data +
                  '</a>';
              } else if (row.change == 0) {
                data =
                  '<a style="color:#2a76e8;" href="' +
                  row.scrip +
                  '/' +
                  row.trading_code +
                  '">' +
                  data +
                  '</a>';
              } else {
                data =
                  '<a style="color:green;" href="' +
                  row.scrip +
                  '/' +
                  row.trading_code +
                  '">' +
                  data +
                  '</a>';
              }
            }

            return data;
          },
        },
        {
          title: 'LTP',
          data: 'ltp',
        },
        {
          title: 'CLOSEP',
          data: 'closep',
        },
        {
          title: 'CHANGE',
          data: 'change',
          render: function (data, type, row, meta) {
            if (type === 'display') {
              if (row.change < 0) {
                data = '<span style="color:red;">' + data + '</span>';
              } else if (row.change == 0) {
                data = '<span style="color:#2a76e8;">' + data + '</span>';
              } else {
                data = '<span style="color:green;">' + data + '</span>';
              }
            }

            return data;
          },
        },
        {
          title: 'YCP',
          data: 'ycp',
        },
      ],
    };
  }

  renderIndiceGraph(index: string): void{
    const options = {
      hour12: false, // use 24-hour clock
      hour: "2-digit",
      minute: "2-digit"
     };

     this.receiveDseIndices().subscribe((data1) => {
      const data2=data1[index];
      console.log(data2);

      const data = Object.entries(data2).map(([x, y]) => ({ x: parseInt(x), y: y }));

      console.log(data);
      this.lineGraph={
        chart: {
          type: 'area',
          height: '100%',
          width: '100%',
          zoom: {
            type: 'x',
            enabled: true,
            autoScaleYaxis: true,
          },
        },
        series: [{
          name: 'Index',
          data: data,
        }],
        xaxis: {
          type: 'datetime',
          labels: {
            format: 'h:mm',
          }},
        fill: {
            colors: ['#ffffff'],
            type: 'gradient',
            gradient: {
              shadeIntensity: 1,
              inverseColors: false,
              opacityFrom: 0.7,
              opacityTo: 0.6,
              stops: [0, 90, 100],
            },
          },
          grid: {
            borderColor: '#f1f1f1',
          },

      };
      this.lineGraph.render();
  }) 
}

renderPieChart(): void {

  this.receiveMarketData().subscribe((data : company[]) => {

  for (let i = 0; i < data.length; i++) {
    if(data[i].change>0){
      this.y[0]++;
    }
    else if(data[i].change<0){
      this.y[1]++;
    }
    else{this.y[2]++;}
  }

  const categories = ['Gainers','Losers','Neutral'];
  const colours=['#00FF00', '#FF0000', '#0000FF'];
  console.log(this.y);
  this.pieChart={
    chart: {  
      type: 'pie',
      height: '100%',
      width: '100%',
    },
    series: this.y,
    labels :categories,
    colors: colours

  };
})


}}
