    return this.MarketDataService.getMarketData();
  }

  receiveDseIndices(): Observable<any> {
    return this.MarketDataService.getIndices();
  }

  receiveNews(): void {
    this.NewsService.getNews()
      .subscribe({
        next: (res: any) => {
          console.log(res);
          this.newsData = res;
        },
        error: (e: any) => console.error(e)
