  renderPieChart(): void {
    this.getData().subscribe ((data)=> {
          const categories = ['Institute', 'Foreign', 'Public'];
          this.graphdata=data;
           console.log(this.graphdata.Public);

          this.pieChart = {
            chart: {
              type: 'pie',
              height: '100%',
              width: '100%',
            },
            theme: {
              monochrome: {
                enabled: true
              }
            },
            // series: [44, 55, 13],
            series:[this.graphdata.Institute,this.graphdata.Foreign, this.graphdata.Public],
            labels: categories,

          };
