import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { company, MarketDataService } from 'src/app/services/market-data.service';
import { ChartOptions } from '../home/home.component';
import { TechnicalIndicatorsService } from 'src/app/services/technical-indicators.service';
import { HttpClient } from '@angular/common/http';
import { ActivatedRoute } from '@angular/router';




@Component({
  selector: 'app-company-profile',
  templateUrl: './company-profile.component.html',
  styleUrls: ['./company-profile.component.css']
})
export class CompanyProfileComponent implements OnInit {
  public lineGraph: Partial<ChartOptions> | any;
  public pieChart: Partial<ChartOptions> | any;
  public pieChart2: Partial<ChartOptions> | any;
  y: number[] = Array(3).fill(0);
  dtOptions: DataTables.Settings = {};
  newsData: any;
  NewsService: any;
  code: string = '';
  basicData: any;
  graphdata: any;
  graphdata2: any;
  // graphdata: number[]=[];
  trading_code: any;

  constructor(private MarketDataService: MarketDataService,
    private TechnicalIndicatorsService: TechnicalIndicatorsService,
    private marketdatasservice: MarketDataService,
    route: ActivatedRoute) { 
      route.params.subscribe((params) => {
        this.code = params["trading_code"];
      });
    }


  ngOnInit(): void {
    this.showData();
    this.renderPieChart();
    this.renderPieChart2();
    this.renderDataTable();

  }

  getData(): Observable<any> {
    return this.marketdatasservice.getProfile();
  }


  receiveMarketData(): Observable<any> {
    return this.MarketDataService.getMarketData();
  }

  receiveDseIndices(): Observable<any> {
    return this.MarketDataService.getIndices();
  }



  showData(): void {
    this.getData()
      .subscribe({
        next: (res) => {
          console.log(res);
          this.basicData = res;
        },
        error: (e) => console.error(e)
      });
  }



  // renderIndiceGraph(index: string): void {

  //   this.getProfile(this.code).subscribe((data1) => {
  //     const data2 = data1[index];
  //     const data = Object.entries(data2).map(([x, y]) => ({ x: parseInt(x), y: y }));

  //     this.lineGraph = {
  //       chart: {
  //         type: 'area',
  //         height: '140%',
  //         width: '100%',
  //         zoom: {
  //           type: 'x',
  //           enabled: true,
  //           autoScaleYaxis: true,
  //         },
  //       },
  //       series: [{
  //         name: 'Index',
  //         data: data,
  //       }],
  //       xaxis: {
  //         type: 'datetime',
  //         labels: {
  //           format: 'h:mm',
  //         }
  //       },
  //       fill: {
  //         colors: ['#ffffff'],
  //         type: 'gradient',
  //         gradient: {
  //           shadeIntensity: 1,
  //           inverseColors: false,
  //           opacityFrom: 0.7,
  //           opacityTo: 0.6,
  //           stops: [0, 90, 100],
  //         },
  //       },
  //       grid: {
  //         borderColor: '#f1f1f1',
  //       },

  //     };
  //     this.lineGraph.render();

  //   })
  // }

  renderPieChart(): void {
    this.getData().subscribe ((data)=> {
          const categories = ['Institute', 'Foreign', 'Public'];
          this.graphdata=data;        
          this.pieChart = {
            chart: {
              type: 'pie',
               width: '55%',
            },
            theme: {
              monochrome: {
                enabled: true
              }
            },
            series:[this.graphdata.Institute,this.graphdata.Foreign, this.graphdata.Public], 
           labels: categories,            
          };
        },
       );
     }

     renderPieChart2(): void {
      this.getData().subscribe ((data)=> {
        const categories = ['Authorized Capital: '+this.graphdata.AuthorizedCap, 'Paid Up Capital: '+this.graphdata.PaidUpCap];

            this.graphdata2=data;
            // this.graphdata=[data.Public,data.Foreign,data.Institute];

            this.pieChart2 = {
              chart: {
                type: 'pie',
                 width: '55%',
              },
              theme: {
                monochrome: {
                  enabled: true
                }
              },
              series:[this.graphdata.AuthorizedCap,this.graphdata.PaidUpCap],
             labels: categories,            
            };
          },
         );
       }


  renderDataTable(): void {
    this.dtOptions = {
      searching: false,
     ordering:  false,
      // serverSide: true,     // Set the flag 
      // ajax: (dataTablesParameters: any, callback) => {
      //   that.http
      //     .post<DataTablesResponse>(
      //       'https://xtlncifojk.eu07.qoddiapp.com/',
      //       dataTablesParameters, {}
      //     ).subscribe(res => {
      //       callback({
      //         recordsTotal: res.recordsTotal,
      //         recordsFiltered: res.recordsFiltered,
      //         data: res.data
      //       });
      //     });
      // },
      columnDefs: [
        { width: '90em', targets: 0 },
        { width: '30em', targets: [1, 2, 3, 4, 5] },

      ],
      columns: [
        {
          title: 'Particulars',
          data: '',
        },
        {
          title: '2022',
          data: '2022',
        },
        {
          title: '2021',
          data: '2021',
        },
        {
          title: '2020',
          data: '2020',
        },
        {
          title: '2019',
          data: '2019',
        }, {
          title: '2018',
          data: '2018',
        },
      ],
    };
  }



}
