import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-sign-up',
  templateUrl: './sign-up.component.html',
  styleUrls: ['./sign-up.component.css']
})
export class SignUpComponent {
  phone= null;
  bo= null;
  email= null;
  otp=null;
  password= null;

  constructor(private http: HttpClient) { }

  onSubmit() {
    // Send OTP to user's phone number
    const data = { phoneNumber: this.phone };
    this.http.post('http://localhost:8000/send-otp', data)
      .subscribe((response: any) => {
        alert('OTP sent to your phone number');
      });
  }

  onVerifyOTP() {
    // Verify OTP entered by user
    const data = { phoneNumber: this.phone, otp: this.otp };
    this.http.post('http://localhost:8000/verify-otp', data)
      .subscribe((response: any) => {
        if (response.success) {
          // OTP verification successful, set password and create account
          const data = { accountNumber: this.bo, phoneNumber: this.phone, email: this.email, password: this.password };
          this.http.post('http://localhost:8000/create-account', data)
            .subscribe((response: any) => {
              alert('Account created successfully');
            });
        } else {
          alert('Invalid OTP');
        }
      });
  }
}
