import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import {
  ApexAxisChartSeries,
  ApexChart,
  ApexDataLabels,
  ApexPlotOptions,
  ApexYAxis,
  ApexLegend,
  ApexStroke,
  ApexXAxis,
  ApexFill,
  ApexTooltip,
} from 'ng-apexcharts';


export type ChartOptions = {
  series: ApexAxisChartSeries;
  chart: ApexChart;
  dataLabels: ApexDataLabels;
  plotOptions: ApexPlotOptions;
  yaxis: ApexYAxis;
  xaxis: ApexXAxis;
  fill: ApexFill;
  tooltip: ApexTooltip;
  stroke: ApexStroke;
  legend: ApexLegend;
};

@Component({
  selector: 'app-graph',
  templateUrl: './graph.component.html',
  styleUrls: ['./graph.component.css']
})
export class GraphComponent {

  public lineGraph: Partial<ChartOptions> | any;
  public lineGraph2: Partial<ChartOptions> | any;
  public lineGraph3: Partial<ChartOptions> | any;
  public lineGraph4: Partial<ChartOptions> | any;
  public lineGraph5: Partial<ChartOptions> | any;
  public lineGraph6: Partial<ChartOptions> | any;

  code: string = "GP";
  dateFrom: string = "2022-03-19";

  baseUrl = 'http://localhost:4000/api/MACD/';
  baseUrl2 = 'http://localhost:4000/api/SMA50/';
  baseUrl3 = 'http://localhost:4000/api/EMA50/';
  baseUrl4 = 'http://localhost:4000/api/STOCH/';
  baseUrl5 = 'http://localhost:4000/api/RSI/';
  baseUrl6 = 'http://localhost:4000/api/BB/';

  constructor(private http: HttpClient) { }

  getMACD() {
    this.http.post(this.baseUrl, { code: this.code, dateFrom: this.dateFrom }).subscribe(
      (res) => {
        // const data = Object.entries(res).map(([x, y]) => ({ x: parseInt(x), y: y }));
        // console.log(data);
        this.lineGraph = {
          chart: {
            type: 'area',
            height: '140%',
            width: '100%',
            zoom: {
              type: 'x',
              enabled: true,
              autoScaleYaxis: true,
            },
          },
        //   series: [{
        //     name: 'Open',
        //     data: res.Open,
        //   },
        //   {
        //     name: 'High',
        //     data: res["High"],
        //   },
        //   {
        //     name: 'Low',
        //     data: res["Low"],
        //   },

        // ],
        //   xaxis: {
        //     categories: res["Date"],
        //   },
        };
        this.lineGraph.render();
      })

  }


}
