  // ngOnInit() {
  //   this.form = this.formBuilder.group({
  //     email: [null, [Validators.required, Validators.email]],
  //     password: [null, Validators.required],
  //   });
  // }

  sendPhone() {
    this.http.post(this.baseUrl, { phone: this.Phone }).subscribe(
      (response) => {
        this.showOtp = true;
        this.showPhone = false;
      },
      (error) => {
        console.error('Error:', error);
      }
    );

  }

  sendOtp() {
    this.http.post(this.baseUrl1, { otp: this.OTP }).subscribe({
      next: (res) =>{
        if(res=="1"){
          this.verified=true;
        }
        else{this.verifyOtp=false;}
      },    
      error: (e) => {
        console.log('Error:', e);
      }
    });

  }

matchPassword(){
    if(this.password==this.password2){
      console.log("helloo.......");
      const data = { BO: this.bo, phone: this.Phone, password: this.password };
      this.http.post(this.baseUrl2, { BO: this.bo, phone: this.Phone, password: this.password })
      .subscribe((response: any) => {
        if (response.success) {
          alert('Sign up successful');
        } else {
          alert('Sign up failed: ' + response.error);
        }
      });
    }
    else{
      alert('Password does not match.');
    }

}


