import { Component, OnInit } from '@angular/core';
import { IPO, appliedIPO } from 'src/app/models/ipo.model';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-ipo',
  templateUrl: './ipo.component.html',
  styleUrls: ['./ipo.component.css']
})
export class IPOComponent implements OnInit{
  myForm!: FormGroup;
  isAuthenticated: boolean = true;
  constructor(private modalService: NgbModal,) {}

  selected: IPO = new IPO;
  sendIPO: appliedIPO=new appliedIPO;
  list2:appliedIPO[] = [
    {
      ipo: {
        code: 'AAPL',
        date: new Date(2023, 5, 1),
        price: 200,
        quantity: 1000,
      },
      allocatedQuantity:566,
      applyDate: new Date(2023, 5, 2),
      status: 'Pending',
    },
    {
      ipo: {
        code: 'GOOG',
        date: new Date(2023, 5, 15),
        price: 300,
        quantity: 500,
      },
      allocatedQuantity:56,
      applyDate: new Date(2023, 5, 16),
      status: 'Approved',
    },
    {
      ipo: {
        code: 'AMZN',
        date: new Date(2023, 6, 1),
        price: 150,
        quantity: 2000,
      },
      allocatedQuantity:866,
      applyDate: new Date(2023, 6, 2),
      status: 'Rejected',
    },
  ];



  list: IPO[]=[
    {
      code: 'ABC',
      date: new Date('2023-05-01'),
      price: 10.0,
      quantity: 1000,
    },
    {
      code: 'DEF',
      date: new Date('2023-05-02'),
      price: 12.0,
      quantity: 2000,
    },
    {
      code: 'GHI',
      date: new Date('2023-05-03'),
      price: 8.0,
      quantity: 1500,
    },
  ];


  ngOnInit(): void {
    this.getList();
    this.getHistory();
  }

  getHistory() {
    throw new Error('Method not implemented.');
  }
  getList() {
    throw new Error('Method not implemented.');
  }

  apply(){
    this.sendIPO.ipo=this.selected;
    this.sendIPO.applyDate=new Date();
    this.sendIPO.status='pending';
    //send request
  }

  open(content: any, i: number) {
    this.selected = this.list[i]; 
    this.myForm = new FormGroup({
      'quantity': new FormControl(null, [Validators.required, Validators.min(0), Validators.max(this.selected.quantity)])
    }); 
    this.modalService.open(content);
  }


}
<div class="container">
    <br>
    <h2>Upcoming IPO</h2>
    <br>
    <table class="table table-hover">
      <thead>
        <tr class="table-primary">
          <th scope="col">Code</th>
          <th scope="col">Quantity</th>
          <th scope="col">Price</th>
          <th scope="col">Issue Date</th>
          <th scope="col"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let ipo of list;  let i = index" scope="row">
          <td>{{ ipo.code }}</td>
          <td>{{ ipo.quantity }}</td>
          <td>{{ ipo.price }}</td>
          <td>{{ ipo.date | date: "dd-MM-yyyy" }}</td>
          <td><button (click)="open(content,i)" class="btn btn-primary">Apply</button></td>
        </tr>
      </tbody>
    </table>



    <br>
    <h2>Applied IPO</h2>
    <br>
    <table class="table table-hover">
      <thead>
        <tr class="table-primary">
          <th scope="col">Code</th>
          <th scope="col">Applied Quantity</th>
          <th scope="col">Allocated Quantity</th>
          <th scope="col">Price</th>
          <th scope="col"> Apply Date</th>
          <th scope="col">Status</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let ipo of list2" scope="row">
          <td>{{ ipo.ipo.code }}</td>
          <td>{{ ipo.ipo.quantity }}</td>
          <td>{{ ipo.allocatedQuantity }}</td>
          <td>{{ ipo.ipo.price }}</td>
          <td>{{ ipo.applyDate| date: "dd-MM-yyyy" }}</td>
          <td>{{ ipo.status }}</td>

        </tr>
      </tbody>
    </table>


  </div>

  <ng-template #content let-c="close" let-d="dismiss">
    <div class="modal-header">
      <button type="button" class="btn-close" aria-label="Close" (click)="d('Cross click')"></button>
    </div>
    <div class="modal-body">
      <form  [formGroup]="myForm">
        <div class="form-group">
          <label for="code">Code:</label>
          <input type="text" class="form-control" id="code" placeholder="{{selected.code}}" disabled>
        </div>

        <div class="form-group">
          <label for="price">Price:</label>
          <input type="number" class="form-control" id="price" placeholder="{{selected.price}}" disabled>
        </div>

        <div class="form-group">
          <label for="quatity">Quantity:</label>
          <input type="number" class="form-control" id="quatity"  formControlName="quantity" [(ngModel)]="sendIPO.ipo.quantity" name="quatity"
           placeholder="{{selected.quantity}}" required max="{{selected.quantity}}" min="1">
           <div
           *ngIf="myForm.controls['quantity'].invalid && (myForm.controls['quantity'].dirty || myForm.controls['quantity'].touched)">
           <small *ngIf="myForm.controls['quantity'].hasError('required')">Quantity is required.</small>
           <small *ngIf="myForm.controls['quantity'].hasError('min')">Quantity must be a positive
               value.</small>
           <small *ngIf="myForm.controls['quantity'].hasError('max')">Quantity cannot be greater than
               {{selected.quantity}}.</small>
       </div>
        </div>


        <div class="col-md-12">
          <br>
          <button type="submit" [disabled]="myForm.invalid" (click)="apply(); d('Cross click')" class="btn btn-success">Make Request</button>
          <br><br><br><br>
        </div>
      </form>
    </div>

  </ng-template>
