import { Component, OnInit } from '@angular/core';
import { IPO } from 'src/app/models/ipo.model';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-ipo',
  templateUrl: './ipo.component.html',
  styleUrls: ['./ipo.component.css']
})
export class IPOComponent implements OnInit{
  constructor(private modalService: NgbModal,) {}

  selected: IPO = new IPO;
  list: IPO[]=[
    {
      code: 'ABC',
      date: new Date('2023-05-01'),
      price: 10.0,
      quantity: 1000,
    },
    {
      code: 'DEF',
      date: new Date('2023-05-02'),
      price: 12.0,
      quantity: 2000,
    },
    {
      code: 'GHI',
      date: new Date('2023-05-03'),
      price: 8.0,
      quantity: 1500,
    },
  ];


  ngOnInit(): void {
    this.getList();
  }
  getList() {
    throw new Error('Method not implemented.');
  }

  apply(){

  }
  open(content: any, i: number) {
    this.selected = this.list[i];  
    this.modalService.open(content);
  }


}
<div class="container">
    <br>
    <h2>Upcoming IPO</h2>
    <br>
    <table class="table table-hover">
      <thead>
        <tr class="table-primary">
          <th scope="col">Code</th>
          <th scope="col">Quantity</th>
          <th scope="col">Price</th>
          <th scope="col">Issue Date</th>
          <th scope="col"></th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let ipo of list;  let i = index" scope="row">
          <td>{{ ipo.code }}</td>
          <td>{{ ipo.quantity }}</td>
          <td>{{ ipo.price }}</td>
          <td>{{ ipo.date | date: "dd-MM-yyyy" }}</td>
          <td><button (click)="open(content,i)" class="btn btn-primary">Apply</button></td>
        </tr>
      </tbody>
    </table>
  </div>

  <ng-template #content let-c="close" let-d="dismiss">
    <div class="modal-header">
      <button type="button" class="btn-close" aria-label="Close" (click)="d('Cross click')"></button>
    </div>
    <div class="modal-body">
      <form>
        <div class="form-group">
          <label for="code">Code:</label>
          <input type="text" class="form-control" id="code" placeholder="{{selected.code}}" disabled>
        </div>

        <div class="form-group">
          <label for="price">Price:</label>
          <input type="number" class="form-control" id="price" placeholder="{{selected.price}}" disabled>
        </div>

        <div class="form-group">
          <label for="total">Quantity:</label>
          <input type="number" class="form-control" id="total" [(ngModel)]="selected.quantity" name="total"
           placeholder="{{selected.quantity}}" required>
        </div>


        <div class="col-md-12">
          <br>
          <button type="submit" (click)="apply(); d('Cross click')" class="btn btn-success">Make Request</button>
          <br><br><br><br>
        </div>
      </form>
    </div>

  </ng-template>
