export class Loan {
    ID: number=0;
    amount: number=0;
    payable: number=0;
    term: number=0;
    issueDate: Date= new Date();
    acceptDate: Date= new Date();
    deadline: Date= new Date();
    status: string='';

    constructor(ID: number, amount: number, payable: number, term: number, issueDate: Date, acceptDate: Date, status: string) {
        this.ID = ID;
        this.amount = amount;
        this.payable = payable;
        this.term = term;
        this.issueDate = issueDate;
        this.acceptDate = acceptDate;
        this.status = status;

        // Calculate deadline by adding term in months with issue date
        const deadline = new Date(this.issueDate);
        deadline.setMonth(deadline.getMonth() + this.term);
        this.deadline = deadline;
      }
}
